"use strict";
function SuppressWarnings(opt, whitelist) {
    if (!opt)
        opt = [];
    whitelist = !!whitelist;
    const original = process.emitWarning;
    process.emitWarning = ((...args) => {
        // I know I could probably shorten this and prevent duplication but I don't feel like it right now
        for (const f of opt) {
            if (!whitelist) {
                if (f(...args))
                    return;
                else
                    continue;
            }
            else {
                if (!f(...args))
                    return;
                else
                    continue;
            }
        }
        return original(...args);
    });
}
module.exports = SuppressWarnings;
